		$criteria->select	 = "*,DATE_ADD(bkg_pickup_date,INTERVAL bkg_trip_duration HOUR) as cab_free_time";
		if ($date1 != '' && $date2 != '')
		{
			$criteria->addBetweenCondition('DATE(DATE_ADD(bkg_pickup_date,INTERVAL bkg_trip_duration HOUR))', $date1, $date2, 'AND');
