			$hashBkgId											 = Yii::app()->shortHash->hash($val['bkg_id']);
			$hashVndId											 = Yii::app()->shortHash->hash($vendorId);
//$recordset[$key]['bkvn_url']	 ='http://gozo-server.ddns.net:6172'.'/bkvn/'. $hashBkgId . '/' . $hashVndId;
