#$condition		 = "  AND ( DATE(bkg_pickup_date) BETWEEN DATE_SUB(CURDATE(), INTERVAL 5 DAY) AND DATE_ADD(CURDATE(), INTERVAL 5 DAY) )";
//$custMaskedPhone = Yii::app()->params['driverToCustomer'];
