				. "AND (DATE(a.bkg_create_date) = '" . date('Y-m-d', strtotime($model->bkg_create_date)) . "' OR DATE(a.bkg_pickup_date) = '" . date('Y-m-d', strtotime($model->bkg_pickup_date)) . "') "
