		$date		 = date('Y-m-d H:i:s');
		$sql		 = "SELECT bkg_id FROM `booking` INNER JOIN `booking_pref` ON booking.bkg_id=booking_pref.bpr_bkg_id WHERE bkg_id IN (" . $bookingIds . ") AND bkg_account_flag=0";
