	public function extractPincodeFromAddress($address)
	{
		$pincode = '';
		if ($address != '')
		{
			preg_match('/(?<![0-9])[0-9]{6}(?![0-9])/', $address, $matches);
			$pincode = $matches[0];
		}
		return $pincode;
	}

