					$model->bkgInvoice->bkg_sgst = Yii::app()->params['sgst'];
					$model->bkgInvoice->bkg_igst = 0;
				}
				else
				{
					$model->bkgInvoice->bkg_igst = Yii::app()->params['igst'];
					$model->bkgInvoice->bkg_cgst = 0;
