			LEFT JOIN contact_phone phn ON phn.phn_contact_id = ctt.ctt_id AND phn.phn_is_primary = 1 AND phn.phn_active = 1
			LEFT JOIN svc_class_vhc_cat scv ON bkg.bkg_vehicle_type_id = scv.scv_id
