		{
			ServiceCallQueue::autoCloseRelatedLeadQuote($bkgModel->bkg_id);
		}
		Logger::profile("got $bkgModel->bkg_id");
		Logger::unsetModelCategory(__CLASS__, __FUNCTION__);
		return $bkgModel->bkg_id;
	}

