				$recordset[$key]['bkg_pickup_date'] = BookingCab::model()->getPickupDateTime("Y-m-d H:i:s", $recordset[$key]['bkg_pickup_date'], $uberAgentId);
			}
			if ($val['bkg_id'] > 0)
