			if ($val['bkg_agent_id'] > 0 && $val['is_driver_app_required'] == 0)
			{
				$agentModel									 = Agents::model()->findByPk($val['bkg_agent_id']);
				$recordset[$key]['is_driver_app_required']	 = $agentModel->agt_driver_app_required;
			}
			if ($val['is_cng_allowed'] > 0)
