		$sql			 = "SELECT 
				bkg_id,
				CASE
					 WHEN bcb_assign_mode = 0 THEN 'AUTO'
					 WHEN bcb_assign_mode = 1 THEN 'MANUAL' 
					 WHEN bcb_assign_mode = 2 THEN 'DIRECT ACCEPT'
					 ELSE '-'
				END AS 'assigned_mode',
				booking_trail.bkg_assigned_at,
				admins.gozen,
