			fputcsv($handle, $line);
		}
		fclose($handle);

		//$toArr = ['salesreporting@kayak.com', 'ar@kayak.com', 'tarun@kayak.com', 'upatel@kayak.com', 'gaurav@gozo.cab', 'kaushal.goenka@gozo.cab'];
